SELECT MEMNO, NINO, INITIALS, SURNAME, DOB, NRD, SPAD, DJS, COALESCE(SCHEMECASH,0) AS SchemeCash, COALESCE(TOTEECONTSEXIT,0) AS TotalContsAtExit, COALESCE(SCALEPENSION,0) AS ScalePension, COALESCE(FINALREMUN,0) AS FinalRemuneration, COALESCE(FPSEXIT,0) AS FPSAtExit, IncDate,
			COALESCE([20],0)AS Item20,COALESCE([21],0) AS Item21,COALESCE([22],0) AS Item22,COALESCE([23],0) AS Item23,COALESCE([24],0) AS Item24,COALESCE([25],0) AS Item25,COALESCE([26],0) AS Item26,COALESCE([27],0) AS Item27,COALESCE([28],0) AS Item28,COALESCE([29],0) AS Item29,
			COALESCE([30],0)AS Item30,COALESCE([31],0) AS Item31,COALESCE([32],0) AS Item32,COALESCE([33],0) AS Item33,COALESCE([34],0) AS Item34,COALESCE([35],0) AS Item35,COALESCE([36],0) AS Item36,COALESCE([37],0) AS Item37,COALESCE([38],0) AS Item38,COALESCE([39],0) AS Item39,
			COALESCE([40],0)AS Item40,COALESCE([41],0) AS Item41,COALESCE([42],0) AS Item42,COALESCE([43],0) AS Item43,COALESCE([44],0) AS Item44,COALESCE([45],0) AS Item45,COALESCE([46],0) AS Item46,COALESCE([47],0) AS Item47,COALESCE([48],0) AS Item48,COALESCE([49],0) AS Item49,
			COALESCE([50],0)AS Item50,COALESCE([51],0) AS Item51,COALESCE([52],0) AS Item52,COALESCE([53],0) AS Item53,COALESCE([54],0) AS Item54,COALESCE([55],0) AS Item55,COALESCE([56],0) AS Item56,COALESCE([57],0) AS Item57,COALESCE([58],0) AS Item58,COALESCE([59],0) AS Item59,
			COALESCE([60],0)AS Item60,COALESCE([61],0) AS Item61,COALESCE([62],0) AS Item62,COALESCE([63],0) AS Item63,COALESCE([64],0) AS Item64,COALESCE([65],0) AS Item65,COALESCE([66],0) AS Item66,COALESCE([67],0) AS Item67,COALESCE([68],0) AS Item68,COALESCE([69],0) AS Item69,
			COALESCE([70],0)AS Item70,COALESCE([71],0) AS Item71,COALESCE([72],0) AS Item72,COALESCE([73],0) AS Item73,COALESCE([74],0) AS Item74,COALESCE([75],0) AS Item75,COALESCE([76],0) AS Item76,COALESCE([77],0) AS Item77,COALESCE([78],0) AS Item78,COALESCE([79],0) AS Item79,
			COALESCE([80],0)AS Item80,COALESCE([81],0) AS Item81,COALESCE([82],0) AS Item82,COALESCE([83],0) AS Item83,COALESCE([84],0) AS Item84,COALESCE([85],0) AS Item85,COALESCE([86],0) AS Item86,COALESCE([87],0) AS Item87,COALESCE([88],0) AS Item88,COALESCE([89],0) AS Item89,
			COALESCE([90],0)AS Item90,COALESCE([91],0) AS Item91,COALESCE([92],0) AS Item92,COALESCE([93],0) AS Item93,COALESCE([94],0) AS Item94,COALESCE([95],0) AS Item95,COALESCE([96],0) AS Item96,COALESCE([97],0) AS Item97,COALESCE([98],0) AS Item98,COALESCE([99],0) AS Item99
FROM 
	(
	SELECT m.Memno, NINO, INITIALS, SURNAME, DOB, NRD, SPAD, DJS, Amount, ItemNo, IncDate, SCHEMECASH, TOTEECONTSEXIT, SCALEPENSION, FINALREMUN, FPSEXIT
	FROM MEMBERS m
	LEFT JOIN PEOPLE p ON m.PEOPLEID = p.ID
	LEFT JOIN EXITDEFERREDS e ON m.MEMNO = e.MEMNO
	LEFT JOIN DEFERREDITEMS d ON m.MEMNO = d.Memno
	WHERE SKEM = 'ABP'
	AND SSTA IN (12,15,65)
) AS SourceTable
PIVOT
(
	SUM(Amount)
	FOR ITEMNO IN ([20],[60],[61],[62],[63],[36],[37],[28],[96],[30],[92],[84],[41],[91],[42],[71],[69],[44],[58],[98],[97],[87],[65],[52],[57],[56],[53],[24],[90],[25],[45],[43],[73],[77],[35],[48],[79],[76],[39],[67],[33],[74],[88],[70],[27],[51],[83],[66],[59],[89],[75],[46],[50],[93],[21],[94],[72],[64],[34],[23],[68],[38],[40],[32],[81],[47],[82],[80],[78],[86],[54],[99],[55],[22],[29],[49],[26],[85],[31],[95])
) AS x
